import { Button } from "../button"
import { useState } from "react"
import rs from "../assets/rs.png"
import "./style.css"

export const Form = () => {

    const [todos, setTodos] = useState([]);

    function addTodo(newTodo) {
        setTodos([...todos, newTodo]);

    }

    const [info, setInfo] = useState("")

    return (
        <form onSubmit={(event) => { event.preventDefault() }}>

            <div className="valueOne">
                <label>Descrição</label>
                <input type="text" placeholder="Digite aqui sua descrição" onChange={(event) => setInfo(event.target.value)} />
                <span>Ex: Compra de roupas</span>
            </div>

            <div className="valueAndType">
                <div className="valueTwo">
                    <label htmlFor="value">Valor</label>
                    <input type="text" name="value" onChange={(event) => setInfo(event.target.value)} />
                    <img src={rs} alt="cifrão" />
                </div>

                <div className="valueTree">
                    <label htmlFor="typeValue">Tipo de valor</label>

                    <select name="typeValue" onChange={(event) => setInfo(event.target.value)} >
                        <option value="entry">Entrada</option>
                        <option value="leave">Despesa</option>
                    </select>
                </div>
            </div>

            <Button type="submit" onClick={() => addTodo(console.log(info))} className="btnRegister">Inserir valor</Button>
        </form>

    )
}


